function ManyFontLoader_Normal_fontLoaded(self, msg) {
    self.current++
    if (self.current == self.faces.length) {
        self.onLoaded()
        self.state = null;
    } else {
        loadNextFont(self)
        self.state = "Normal";
    }
}

function ManyFontLoader_Normal_start(self, msg) {
    self.current = 0
    self.faces = msg
    loadNextFont(self)
    self.state = "Normal";
}

