function checkInputText(text) {
    if (globals.readonly) {
        return globals.translate("ERR_READONLY")
    } else {
        if ((text) && (text.length > Config.MAX_TEXT)) {
            return globals.translate("ERR_TEXT_TOO_LONG")
        } else {
            return null
        }
    }
}

